import React, { Component } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";
import "../Fitnessplan.css";

export default class FitnessplanAdmin extends Component {
  calendarComponentRef = React.createRef();
  state = {
    // courses: [],
    events: [],
    event: {
      title: "",
      date: "",
        },
    
    calendarWeekends: true,
    calendarEvents: [
      // initial event data
      { name: "Event Now", start: new Date() },
    ],
  };

  // componentDidMount() {
  //   fetch("http://localhost:9000/api/fitness")
  //     .then((response) => response.json())
  //     .then((course) => {
  //       this.setState({ courses: course });
  //     });
  // }

  componentDidMount() {
    fetch("http://localhost:9000/api/fitnessevents")
      .then((response) => response.json())
      .then((event) => {
        this.setState({ events: event });
      });
  }

  render() {
    const{ events } = this.state;
    const{ courses } = this.state;
    console.log(courses)
    return (
      <div className="fitnessplan">
        <div className="heading">
        Kursplan
        <div className="heading sub">
        Klicke auf ein Datum, um einen neuen Kurs hinzuzuf√ºgen
        </div>
        <div>
        <button onClick={this.toggleWeekends} className="button add" >Ohne Wochenenden</button>&nbsp;
          {/* <button onClick={this.gotoPast}>go to a date in the past</button> */}
        </div>
        </div>
        <div className="fitness-calendar">
          <FullCalendar
            defaultView="timeGridWeek"
            header={{
              left: "prev,next today",
              center: "name",
              right: "dayGridMonth,timeGridWeek,timeGridDay,listWeek",
            }}
            plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
            // editable={true}  //change length of event by dragging the event down. Change day of event by dragging
            // eventContent={this.renderEventContent}
            // eventDrop={this.handleEventDrop}
            // eventClick={this.handleEventClick}
            // select={this.handleDateSelect}
            //eventClick = {function(calendarEvents, jsEvent, view, resourceObj) {alert(courses.name)}}
            ref={this.calendarComponentRef}
            weekends={this.state.calendarWeekends}
            //events={this.state.calendarEvents}
            events={this.state.events}    //Funktioniert theoretisch. 
            dateClick={this.handleDateClick}
            
 
          />
        </div>
       </div>
    );
  }

  // handleEventClick = (info) => {
  //   alert('Event: ' + info.event.name);
  // }

  handleDateClick = (arg) => {
    var dateStr = prompt('Enter a date in YYYY-MM-DD format');
    var date = new Date(arg.dateStr + 'T00:00:00'); 

    if (!isNaN(date.valueOf())) { // valid?
      fitnessplan.addEvent({
        title: 'dynamic event',
        start: date,
        allDay: true
      });
      alert('Great. Now, update your database...');
    } else {
      alert('Invalid date.');
    }

    //calendarApi.unselect() // clear date selection

    // if (name) {
    //   calendarApi.addEvent({
    //     id:"",
    //     name,
    //     start: selectInfo.startStr,
    //     end: selectInfo.endStr,
    //     allDay: selectInfo.allDay
    //   })
    // }
  }

  // renderEventContent(eventInfo) {
  //   return (
  //     <>
  //       <b>{"hey"}</b>
  //       <i>{"hu"}</i>
  //     </>
  //   )
  // }
  

  toggleWeekends = () => {
    this.setState({
      // update a property
      calendarWeekends: !this.state.calendarWeekends,
    });
  };

  gotoPast = () => {
    let calendarApi = this.calendarComponentRef.current.getApi();
    calendarApi.gotoDate("2020-01-01"); // call a method on the Calendar object
  };

//  handleDateClick = (arg) => {
//     if (
//       window.confirm("Would you like to add an event to " + arg.dateStr + " ?")
//     ) {
//       this.setState({
//         // add new event data
//         events: this.state.events.concat({
//           // creates a new array
//           title: "New Event",
//           start: arg.date,
//           allDay: arg.allDay,
//         }),
//       });
//     }
//   }; 
}
