import React, { Component } from "react";
import { NavLink } from "react-router-dom";

import "./style.css";

class Header extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoggedIn: this.props.isLoggedIn,
    };
  }
  componentDidUpdate(prevProps) {
    if (prevProps.isLoggedIn !== this.props.isLoggedIn) {
      this.setState({ isLoggedIn: this.props.isLoggedIn });
    }
  }

  render() {
    if (this.props.isLoggedIn === true) {
      return (
        <div className="header">
          <div className="container">
            <div className="logo">
              <NavLink to="/">Logo</NavLink>
            </div>
            <div className="navbar">
              <ul>
                <li>
                  <NavLink to="/" exact activeStyle={{ color: "#000" }}>
                    Home
                  </NavLink>
                </li>
                <li>
                  <NavLink
                    to="/FitnessplanAdmin"
                    exact
                    activeStyle={{ color: "#000" }}
                  >
                    Fitnessplan
                  </NavLink>
                </li>
                <li>
                  <NavLink
                    to="/FitnessInfoAdmin"
                    exact
                    activeStyle={{ color: "#000" }}
                  >
                    Fitness Info
                  </NavLink>
                </li>
                <li>
                <NavLink
                  to="/"
                  exact
                  activeStyle={{ color: "#000" }}
                  onClick={this.props.handleLogout}
                >
                  Logout
                </NavLink>
                </li>
              </ul>
            </div>
          </div>
        </div>
      );
    } else {
      return (
        <div className="header">
          <div className="container">
            <div className="logo">
              <NavLink to="/">Logo</NavLink>
            </div>
            <div className="navbar">
              <ul>
                <li>
                  <NavLink to="/" exact activeStyle={{ color: "#2a6496" }}>
                    Home
                  </NavLink>
                </li>
                <li>
                  <NavLink
                    to="/Fitnessplan"
                    exact
                    activeStyle={{ color: "#2a6496" }}
                  >
                    Fitnessplan
                  </NavLink>
                </li>
                <li>
                  <NavLink
                    to="/FitnessInfo"
                    exact
                    activeStyle={{ color: "#2a6496" }}
                  >
                    Fitness Info
                  </NavLink>
                </li>
                <li>
                  <NavLink
                    to="/SignUp"
                    exact
                    activeStyle={{ color: "#2a6496" }}
                  >
                    Sign up
                  </NavLink>
                </li>
                <li>
                  <NavLink
                    to="/LoginTest"
                    exact
                    activeStyle={{ color: "#2a6496" }}
                  >
                    Login
                  </NavLink>
                </li>
              </ul>
            </div>
          </div>
        </div>
      );
    }
  }
}

export default Header;
