import React, { Component } from "react";
import moment from 'moment';
import "../FitnessInfo.css";

export default class FitnessInfoUser extends Component {
  constructor(props) {
    super(props);
    this.state = {
      courseList: [],
    };
  }

  componentDidMount() {
    fetch("http://localhost:9000/api/fitness")
      .then((response) => response.json())
      .then((fitness) => {
        this.setState({ courseList: fitness });
      });
  }

  render() {
    return (
      <div>
      <div className="heading">
        Registrierung
      </div>
        <table>
          <thead>
            <tr>
              <th>Datum</th>
              <th>Name</th>
              <th>Preis</th>
              <th>Beschreibung</th>
            </tr>
          </thead>
          <tbody>
            {this.state.courseList.map((fitness) => (
              <tr key={fitness.id}>
                <td>{moment(fitness.date).format("Do MMM YYYY")} </td>
                <td>{fitness.name} </td>
                <td>{fitness.price}</td>
                <td>{fitness.description}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }
}
