import React, { Component } from "react";
import { Button } from "react-bootstrap";
import { Link } from "react-router-dom";
import moment from 'moment';
import "../FitnessInfo.css";
//import EditCourse from "./EditCourse";

export default class FitnessInfoAdmin extends Component {
  constructor(props) {
    super(props);
    this._deleteCourse = this._deleteCourse.bind(this);

    this.state = {
      courseList: [],

      response: {},
    };
  }

  componentDidMount() {
    fetch("http://localhost:9000/api/fitness")
      .then((response) => response.json())
      .then((fitness) => {
        this.setState({ courseList: fitness});
      });
  }

  _deleteCourse(id) {
    const { courseList } = this.state;
    fetch("http://localhost:9000/api/fitness/" + id, {
      method: "DELETE",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
    })
      .then(() => {
        this.setState({
          courseList: courseList.filter((fitness) => fitness.id !== id),
        });
        return;
      })
      .catch((error) => {
        throw error;
      });
    //    fitness.preventDefault();

    console.log("Deleted");
  }

  render() {
    return (
      <div>
        <div className="heading">
        Kursplan
        
        <div className="heading sub">
        Um einen neuen Kurs hinzuzufügen, klicke hier:
        </div>
        <div>
          <Link className="create-link" to={"/AddCourse"}>
            <Button className="button add">Add</Button>
          </Link>
        </div>
        </div>
        <table>
        {/* <caption>Kursplan</caption> */}
          <thead>
            <tr>
              <th>Datum</th>
              <th>Name</th>
              <th>Preis</th>
              <th>Beschreibung</th>
            </tr>
          </thead>
          <tbody>
            {this.state.courseList.map((fitness) => (
              <tr key={fitness.id}>
                <td>{moment(fitness.date).format("Do MMM YYYY")} </td>
                <td>{fitness.name} </td>
                <td>{fitness.price} € </td>
                <td> {fitness.description}</td>
                <div className="last-column">
                <td>
                  <Button className="button delete" variant="danger" onClick={() => this._deleteCourse(fitness.id)}>
                    Delete
                  </Button>
                </td>
                <td>
                  <Link className="edit-link" to={"/EditCourse/" + fitness.id}>
                    <Button className="button edit">Edit</Button>
                  </Link>
                </td>
                </div>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }
}
