import React, { Component } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";
//import { Button } from "react-bootstrap";
import { Link } from "react-router-dom";
//import  Modal  from 'react-bootstrap/Modal';
//import ModalHeader from 'react-bootstrap/ModalHeader';
//import ModalBody from 'react-bootstrap/ModalBody';
//import ModalFooter from 'react-bootstrap/ModalFooter';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from "reactstrap";
import "../Fitnessplan.css";

export default class FitnessplanUser extends Component {
calendarComponentRef = React.createRef();
constructor(props) {
super(props);
this._deleteCourse = this._deleteCourse.bind(this);

this.state = {
    courses: [],
    event: {
      id: "",
      date: "",
      title: "",

      response: {},
    },
    calendarWeekends: true,
    allDay: true,
    calendarEvents: [
      // initial event data
      { name: "Event Now", start: new Date() },
    ],
  };
}

  componentDidMount() {
    fetch("http://localhost:9000/api/courses")
      .then((response) => response.json())
      .then((event) => {
        this.setState({ courses: event });
      });
  }

  render() {
    return (
      <div className="fitnessplan">            
        <div className="heading">
        Kursplan
        <div>
        <button onClick={this.toggleWeekends} className="button add" >Ohne Wochenenden</button>&nbsp;
        </div>
        </div>

        <div className="fitness-calendar">
          <FullCalendar
            defaultView="dayGridMonth"
            header={{
              left: "prev,next today",
              center: "title",
              right: "dayGridMonth,dayGridWeek,listWeek",
            }}

            plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
            ref={this.calendarComponentRef}
            weekends={this.state.calendarWeekends}
            events={this.state.courses} 
          />

        </div>
       </div>
    );
  }


  toggleWeekends = () => {
    this.setState({
      // update a property
      calendarWeekends: !this.state.calendarWeekends,
    });
  };
}
