import React, { Component } from "react";
import { Button } from "react-bootstrap";
import { Link } from "react-router-dom";
import moment from 'moment';
//import EditCourse from "./EditCourse";

export default class FitnessInfo extends Component {
  constructor(props) {
    super(props);
    this._deleteCourse = this._deleteCourse.bind(this);

    this.state = {
      courseList: [],

      response: {},

      courseDate: {},
    };
  }

  componentDidMount() {
    fetch("http://localhost:9000/api/fitness")
      .then((response) => response.json())
      .then((fitness) => {
        this.setState({ courseList: fitness});
        this.setState({courseDate: fitness.date})
      });
  }

  _deleteCourse(id) {
    const { courseList } = this.state;
    fetch("http://localhost:9000/api/fitness/" + id, {
      method: "DELETE",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
    })
      .then(() => {
        this.setState({
          courseList: courseList.filter((fitness) => fitness.id !== id),
        });
        return;
      })
      .catch((error) => {
        throw error;
      });
    //    fitness.preventDefault();

    console.log("Deleted");
  }

  render() {
    const newDate = this.state.courseDate;
    const day = newDate.format("Do MMM YYYY");

    return (
      <div>
        <div>
          <Link className="create-link" to={"/AddCourse"}>
            <Button>Add</Button>
          </Link>
        </div>
        <table>
          <thead>
            <tr>
              <th>Datum</th>
              <th>Name</th>
              <th>Preis</th>
              <th>Beschreibung</th>
            </tr>
          </thead>
          <tbody>
            {this.state.courseList.map((fitness) => (
              <tr key={fitness.id}>
                <td>{day} </td>
                <td>{fitness.name} </td>
                <td>{fitness.price} € </td>
                <td> {fitness.description}</td>
                <td>
                  <Button
                    variant="danger"
                    onClick={() => this._deleteCourse(fitness.id)}
                  >
                    Delete
                  </Button>
                </td>

                <td>
                  <Link className="edit-link" to={"/EditCourse/" + fitness.id}>
                    <Button>Edit</Button>
                  </Link>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }
}
