import React, { Component } from "react";

class Home extends Component {
  state = {
    courses: [],
    selectedCourse: "",
    validationError: "",
  };

  componentDidMount() {
    fetch("http://localhost:9000/api/fitness/" + this.props.match.params.id)
      .then((response) => response.json())
      .then((course) => {
        let coursesFromApi = courses.map(course => {
          return {value: course, display: course}
        });
        this.setState({
          courses: [{value: '', display: '(Select your favourite team)'}].concat(coursesFromApi)
        });
      }).catch(error => {
        console.log(error);
      });
  }

  

  render() {
    return (
      <div>
        <select
        value={this.state.selectedCourse}
        onChange={e => this.setState({selectedCourse: e.target.value,validationError: e.target.value === "" ? "You must select your favourite team" : ""})}
        >
        {this.state.courses.map((course) => <option key={course.value} value={course.value}>{course.display}</option>)}
        </select>
      <div style={{color: 'red', marginTop: '5px'}}>
      {this.state.validationError}
     </div>
     </div>
    ); 
  }
}

