import React, { Component } from "react";
//import { Table, Button, Alert } from "react-bootstrap";
import { Link } from "react-router-dom";

//Hier muss nach dem hinzufügen wieder zur FitnessInfo Seite weitergeleitet werden
export default class AddCourseDateDate extends Component {
  constructor(props) {
    super(props);
    this.onChangeKursDate = this.onChangeKursDate.bind(this);
    
    this.state = {
      course: {
        date: "",
        submitted: false,
      },
    };
  }

  componentDidMount() {}
  onChangeKursDate(e) {
    this.setState({
      date: e.target.value
    });
  }

  handleSubmit() {
    //fitness.preventDefault();
    fetch("http://localhost:9000/api/AddCourseDate", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        date: this.state.date,
        submitted: true,
      }),
    })
      .then((res) => res.json())
      .then((data) => console.log(data))
      .catch((err) => console.log(err));
  }

  render() {
    return (
      <div className="form">
      <div className="heading">
        Füge einen Kurs hinzu:
      </div>
          <div className="form-group">
          <label htmlFor="date">StartDatum</label>
          <input
            type="date"
            className="form-control"
            id="startdatum"
            required
            //value={this.state.name}
            onChange={this.onChangeKursDate}
            name="date"
          />
        </div>


        <Link to="/FitnessPlanAdmin">
          <button onClick={this.handleSubmit} className="button add add2">
            Submit
          </button>
        </Link>
      </div>
    );
  }
}
