import React, { Component } from "react";
import { NavLink } from "react-router-dom";
import logo from './logo.png';
import "./style.css";

console.log(logo);

class Header extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoggedIn: this.props.isLoggedIn,
    };
  }
  componentDidUpdate(prevProps) {
    if (prevProps.isLoggedIn !== this.props.isLoggedIn) {
      this.setState({ isLoggedIn: this.props.isLoggedIn });
    }
  }

  render() {
    if (this.props.isLoggedIn === true) {
      return (
        <div className="header">
          <div className="container">
            <div className="logo">
            <img src={logo} alt="Logo" />;
            </div>
            <div className="navbar">
              <ul>
                <li>
                  <NavLink to="/" exact activeStyle={{ color: "#000" }}>
                    Home
                  </NavLink>
                </li>
                <li>
                  <NavLink
                    to="/FitnessplanAdmin"
                    exact
                    activeStyle={{ color: "#000" }}
                  >
                    Fitnessplan
                  </NavLink>
                </li>
                <li>
                  <NavLink
                    to="/FitnessInfoAdmin"
                    exact
                    activeStyle={{ color: "#000" }}
                  >
                    Fitness Info
                  </NavLink>
                </li>
                <li>
                <NavLink
                  to="/"
                  exact
                  activeStyle={{ color: "#000" }}
                  onClick={this.props.handleLogout}
                >
                  Logout
                </NavLink>
                </li>
              </ul>
            </div>
          </div>
        </div>
      );
    } else {
      return (
        <div className="header">
          <div className="container">
            <div className="logo">
            <img src={logo} alt="Logo" />;
            </div>
            <div className="navbar">
              <ul>
                <li>
                  <NavLink to="/" exact activeStyle={{ color: "#000" }}>
                    Home
                  </NavLink>
                </li>
                <li>
                  <NavLink
                    to="/Fitnessplan"
                    exact
                    activeStyle={{ color: "#000" }}
                  >
                    Fitnessplan
                  </NavLink>
                </li>
                <li>
                  <NavLink
                    to="/FitnessInfo"
                    exact
                    activeStyle={{ color: "#000" }}
                  >
                    Fitness Info
                  </NavLink>
                </li>
                <li>
                  <NavLink
                    to="/SignUp"
                    exact
                    activeStyle={{ color: "#000" }}
                  >
                    Sign up
                  </NavLink>
                </li>
                <li>
                  <NavLink
                    to="/LoginTest"
                    exact
                    activeStyle={{ color: "#000" }}
                  >
                    Login
                  </NavLink>
                </li>
              </ul>
            </div>
          </div>
        </div>
      );
    }
  }
}

export default Header;
