import React, { Component } from "react";
import { Button } from "react-bootstrap";
import { Link } from "react-router-dom";
import moment from 'moment';
//import EditCourse from "./EditCourse";

export default class FitnessInfoAdmin extends Component {
  constructor(props) {
    super(props);
    this._deleteCourse = this._deleteCourse.bind(this);

    this.state = {
      courses: [],

      response: {},
    };
  }

  componentDidMount() {
    fetch("http://localhost:9000/api/fitness")
      .then((response) => response.json())
      .then((course) => {
        this.setState({ courses: course});
      });
  }

  _deleteCourse(id) {
    const { courses } = this.state;
    fetch("http://localhost:9000/api/fitness/" + id, {
      method: "DELETE",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
    })
      .then(() => {
        this.setState({
          courses: courses.filter((course) => course.id !== id),
        });
        return;
      })
      .catch((error) => {
        throw error;
      });
    //    fitness.preventDefault();

    console.log("Deleted");
  }

  render() {
    return (
      <div>
        <div className="heading">
        Kursplan
        <div className="heading sub">
        Um einen neuen Kurs hinzuzufügen, klicke hier:
        </div>
        <div>
          <Link className="create-link" to={"/AddCourse"}>
            <Button className="button add">Add</Button>
          </Link>
        </div>
        </div>
        <table id="info">
          <thead>
            <tr>
              <th>StartDatum</th>
              <th>StartDatum</th>
              <th>Name</th>
              <th>Preis</th>
              <th>Beschreibung</th>
              <th colSpan="2">Bearbeiten</th>
            </tr>
          </thead>
          <tbody>
            {this.state.courses.map((course) => (
              <tr key={course.id}>
                <td>{moment(course.date).format("Do MMM YYYY")} </td>
                <td>{moment(course.date).format("Do MMM YYYY")} </td>
                <td>{course.title} </td>
                <td>{course.price} € </td>
                <td> {course.description}</td>
                <td>
                <div className="last-column">
                  <Button className="button delete" variant="danger" onClick={() => this._deleteCourse(course.id)}>
                    Delete
                  </Button>
                </div>
                </td>
                <td>
                <div className="last-column">
                  <Link className="edit-link" to={"/EditCourse/" + course.id}>
                    <Button className="button edit">Edit</Button>
                  </Link>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        </div>
    );
  }
}
