import React, { Component } from "react";
import { NavLink } from "react-router-dom";

import Login from "./Login";
import "./style.css";

class Header extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showLoginForm: false,
    };
  }

  loginForm = () => {
    this.setState({
      showLoginForm: true,
    });
  };

  loginClose = () => {
    this.setState({
      showLoginForm: false,
    });
  };

  render() {
    const { showLoginForm } = this.state;
    return (
      <div className="header">
        <div className="container">
          <div className="logo">
            <NavLink to="/">Logo</NavLink>
          </div>
          <div className="navbar">
            <ul>
              <li>
                <NavLink to="/" exact activeStyle={{ color: "#2a6496" }}>
                  Home
                </NavLink>
              </li>
              <li>
                <NavLink
                  to="/Essensplan"
                  exact
                  activeStyle={{ color: "#2a6496" }}
                >
                  Essensplan
                </NavLink>
              </li>
              <li>
                <NavLink
                  to="/FitnessInfo"
                  exact
                  activeStyle={{ color: "#2a6496" }}
                >
                  Essen Details
                </NavLink>
              </li>
              <li>
                <NavLink to="/SignUp" exact activeStyle={{ color: "#2a6496" }}>
                  Sign up
                </NavLink>
              </li>
              <li>
                <NavLink
                  to="/LoginTest"
                  exact
                  activeStyle={{ color: "#2a6496" }}
                >
                  Login
                </NavLink>
              </li>
              <li>
                <button className="cursor" onClick={this.loginForm}>
                  Login
                </button>
              </li>
            </ul>
          </div>
          <div className="clear"></div>
          {showLoginForm ? <Login onClose={this.loginClose} /> : null}
        </div>
      </div>
    );
  }
}

export default Header;
