import React, { Component } from "react";
//import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import { Link } from "react-router-dom";

//Hier muss nach dem hinzufügen wieder zur FitnessInfo Seite weitergeleitet werden
export default class EditCourse extends Component {
  constructor(props) {
    super(props);

    this.handleInputChange = this.handleInputChange.bind(this);
    // this.onChangeCourseDate = this.onChangeCourseDate.bind(this);
    // this.onChangeCourseName = this.onChangeCourseName.bind(this);
    // this.onChangeCoursePrice = this.onChangeCoursePrice.bind(this);
    // this.onChangeCourseDescription = this.onChangeCourseDescription.bind(this);
    // this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      currentCourse: [],
      fitness: {
        date: "",
        name: "",
        price: null,
        description: "",
      },
      
    };
  }

  handleInputChange(event) {
    event.preventDefault();
    const target = event.target;
    const value = target.value;
    const name = target.name;
 
    this.setState({
      [name]:value
    })
  }

  componentDidMount() {
    fetch("http://localhost:9000/api/fitness/" + this.props.match.params.id)
      .then((response) => response.json())
      .then((data) =>
        this.setState({
          currentCourse: data,
        })
      );
  }

  onChangeCourseDate(e) {
    this.setState({ date: e.target.value });
  }

  onChangeCourseName(e) {
    this.setState({ name: e.target.value });
  }

  onChangeCoursePrice(e) {
    this.setState({ price: e.target.value });
  }

  onChangeCourseDescription(e) {
    this.setState({ description: e.target.value });
  }

  onSubmit() {
    //e.preventDefault();
    fetch("http://localhost:9000/api/fitness/" + this.props.match.params.id, {
      method: "PUT",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        date: this.state.date,
        name: this.state.name,
        price: this.state.price,
        description: this.state.description,
      }),
    })
      .then((res) => res.json())
      .catch((error) => {
        throw error;
      });

    console.log("Edited");
  }

  //Hier müssen noch Name, Preis und Beschreibung des zu bearbeitenden fitnesss mitgegeben werden, evtl. über value
  render() {
    // console.log(this.state.hallo);
    //console.log(this.state.currentCourse);
    return (
      <div>
        <div className="form-group">
        <label htmlFor="date">Datum</label>
        {this.state.currentCourse.map((fitness) => (
          <input
            key={fitness.id}
            type="text"
            className="form-control"
            id="datum"
            defaultValue={fitness.date}
            onChange={this.onChangeCourseDate}
            name="date"
          />
        ))}
      </div>

        <div className="form-group">
          <label htmlFor="name">Name</label>
          {this.state.currentCourse.map((fitness) => (
            <input
              key={fitness.id}
              type="text"
              className="form-control"
              id="name"
              defaultValue={fitness.name}
              onChange={this.onChangeCourseName}
              name="name"
            />
          ))}
        </div>

        <div className="form-group">
          <label htmlFor="price">Preis</label>
          {this.state.currentCourse.map((fitness) => (
            <input
              key={fitness.id}
              type="number"
              min="0"
              step="1"
              className="form-control"
              id="preis"
              defaultValue={fitness.price}
              onChange={this.onChangeCoursePrice}
              name="price"
            />
          ))}
        </div>

        <div className="form-group">
          <label htmlFor="description">Description</label>
          {this.state.currentCourse.map((fitness) => (
            <input
              key={fitness.id}
              type="text"
              className="form-control"
              id="description"
              defaultValue={fitness.description}
              onChange={this.onChangeCourseDescription}
              name="description"
            />
          ))}
        </div>

        <Link to="/FitnessInfo">
          <Button
            variant="danger"
            type="submit"
            onClick={() => this.onSubmit()}
          >
            Update Kurs
          </Button>
        </Link>
      </div>
    );
  }
}
